INSERT INTO article_sections (article_id, section_type, section_order, title, content) VALUES
('a1100013-0000-0000-0000-000000000013'::uuid, 'introduction', 1, 'Introduction', 'Cardiopulmonary resuscitation (CPR) is a life-saving technique used when the heart stops beating. CPR keeps oxygenated blood flowing to the brain and vital organs until definitive treatment can restore a normal heart rhythm. Key components include high-quality chest compressions, early defibrillation, and advanced life support interventions.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 'content', 2, 'Basic Life Support', 'BLS SEQUENCE: Check responsiveness, Call for help, Check pulse (carotid <10 sec), Begin CPR. HIGH-QUALITY CPR: Rate 100-120/min, Depth 5-6cm (2-2.4in), Full chest recoil, Minimize interruptions <10 sec, Rotate compressors every 2 min. COMPRESSION:VENTILATION RATIO: Adult 30:2 (1 or 2 rescuers), Child 30:2 (1 rescuer) or 15:2 (2 rescuers). AED: Apply as soon as available, Minimize interruption for rhythm check, Resume CPR immediately after shock.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 'content', 3, 'Advanced Cardiac Life Support', 'SHOCKABLE RHYTHMS: VF and pulseless VT. Give shock, immediate CPR 2 min, epinephrine every 3-5 min, amiodarone after 3rd shock. NON-SHOCKABLE RHYTHMS: Asystole and PEA. CPR, epinephrine every 3-5 min, identify reversible causes. EPINEPHRINE: 1mg IV/IO every 3-5 min. AMIODARONE: 300mg first dose, 150mg second dose. REVERSIBLE CAUSES (Hs and Ts): Hypoxia, Hypovolemia, H+ (acidosis), Hypo/Hyperkalemia, Hypothermia, Tension pneumothorax, Tamponade, Toxins, Thrombosis (PE/MI).'),
('a1100013-0000-0000-0000-000000000013'::uuid, 'content', 4, 'Post-Cardiac Arrest Care', 'ROSC MANAGEMENT: Optimize oxygenation (SpO2 94-98%), Avoid hyperventilation, Maintain MAP >65, Consider targeted temperature management (32-36C for 24h), 12-lead ECG for STEMI, Consider PCI, Treat underlying cause.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 'key_points', 5, 'Key Points', '1. CPR rate 100-120 compressions/min. 2. Compression depth 5-6cm in adults. 3. Minimize interruptions (<10 sec). 4. Rotate compressors every 2 min. 5. Compression:ventilation 30:2 for adults. 6. Epinephrine 1mg IV every 3-5 min. 7. Amiodarone 300mg for refractory VF/VT. 8. Defibrillation for VF/pVT only. 9. Hs and Ts are reversible causes. 10. Post-ROSC: avoid hyperoxia/hyperventilation.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('a1100013-0000-0000-0000-000000000013'::uuid, 1, 'Recommended chest compression rate is:', '60-80/min', '80-100/min', '100-120/min', '120-140/min', '140-160/min', 'C', 'AHA recommends 100-120 compressions per minute.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 2, 'Compression depth for adults is:', '2-3 cm', '4-5 cm', '5-6 cm', '7-8 cm', '8-10 cm', 'C', 'Adult compression depth is 5-6 cm (2-2.4 inches).'),
('a1100013-0000-0000-0000-000000000013'::uuid, 3, 'Compression:ventilation ratio for adult CPR by 1 rescuer:', '15:1', '15:2', '30:1', '30:2', '50:2', 'D', 'Adult CPR uses 30:2 ratio.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 4, 'Shockable rhythms include:', 'Asystole and PEA', 'VF and pulseless VT', 'Sinus bradycardia', 'Atrial fibrillation', 'Complete heart block', 'B', 'VF and pulseless VT are shockable rhythms.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 5, 'Epinephrine dose in cardiac arrest is:', '0.1 mg', '0.5 mg', '1 mg', '5 mg', '10 mg', 'C', 'Epinephrine 1mg IV/IO every 3-5 minutes.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 6, 'Epinephrine is given every:', '1-2 min', '3-5 min', '5-10 min', '10-15 min', '15-20 min', 'B', 'Epinephrine given every 3-5 minutes.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 7, 'Amiodarone first dose in refractory VF is:', '150 mg', '200 mg', '300 mg', '450 mg', '600 mg', 'C', 'Amiodarone 300mg first dose, 150mg second dose.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 8, 'Compressors should rotate every:', '1 min', '2 min', '5 min', '10 min', 'No rotation needed', 'B', 'Rotate every 2 minutes to prevent fatigue.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 9, 'Hs and Ts are:', 'Types of arrhythmias', 'Reversible causes of cardiac arrest', 'Defibrillator settings', 'Drug doses', 'CPR quality markers', 'B', 'Hs and Ts are reversible causes of arrest.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 10, 'Which is NOT an H:', 'Hypoxia', 'Hypovolemia', 'Hypothermia', 'Hemorrhage', 'Hyperkalemia', 'D', 'Hemorrhage causes hypovolemia. The Hs include hypoxia, hypovolemia, H+, hypo/hyperkalemia, hypothermia.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 11, 'Post-ROSC target SpO2 is:', '88-92%', '94-98%', '100%', '80-85%', '85-90%', 'B', 'Target SpO2 94-98% to avoid hyperoxia.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 12, 'Maximum time for rhythm check interruption:', '5 sec', '10 sec', '20 sec', '30 sec', '60 sec', 'B', 'Minimize interruptions to <10 seconds.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 13, 'PEA stands for:', 'Pulseless Electrical Activity', 'Primary Electrical Arrest', 'Post-Event Assessment', 'Prolonged Electrical Asystole', 'Paced Electrical Activity', 'A', 'PEA = Pulseless Electrical Activity.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 14, 'Treatment for asystole includes:', 'Defibrillation', 'CPR and epinephrine', 'Amiodarone', 'Cardioversion', 'Atropine only', 'B', 'Asystole is non-shockable. Give CPR and epinephrine.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 15, 'Targeted temperature management range:', '28-30C', '32-36C', '37-38C', '38-40C', '40-42C', 'B', 'TTM targets 32-36C for 24 hours.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 16, 'Chest recoil means:', 'Pressing harder', 'Allowing full return between compressions', 'Leaning on chest', 'Faster compressions', 'Slower compressions', 'B', 'Full recoil allows heart to refill.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 17, 'After shock delivery you should:', 'Check pulse immediately', 'Check rhythm immediately', 'Resume CPR immediately', 'Wait 2 minutes', 'Give epinephrine first', 'C', 'Resume CPR immediately after shock.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 18, 'Pulse check time limit is:', '5 sec', '10 sec', '15 sec', '20 sec', '30 sec', 'B', 'Check carotid pulse for no more than 10 seconds.'),
('a1100013-0000-0000-0000-000000000013'::uuid, 19, 'Tension pneumothorax is which letter:', 'H', 'T', 'Both', 'Neither', 'Sometimes H', 'B', 'Tension pneumothorax is a T (Ts).'),
('a1100013-0000-0000-0000-000000000013'::uuid, 20, 'Post-ROSC hyperventilation should be avoided because:', 'Increases cardiac output', 'Decreases cerebral blood flow', 'Improves oxygenation', 'Treats acidosis', 'Reduces ICP', 'B', 'Hyperventilation decreases cerebral blood flow.');
