INSERT INTO article_sections (article_id, section_type, section_order, title, content) VALUES
('a1100011-0000-0000-0000-000000000011'::uuid, 'introduction', 1, 'Introduction', 'Nasogastric (NG) tube insertion is a common bedside procedure. Types: Ryle''s tube (single lumen), Salem sump (double lumen for suction), feeding tubes (fine bore). Indications: Gastric decompression, Drainage (intestinal obstruction, post-op ileus), Feeding (enteral nutrition), Medication administration, Gastric lavage.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 'content', 2, 'Contraindications and Equipment', 'CONTRAINDICATIONS: Absolute - base of skull fracture, severe midface trauma, esophageal stricture/perforation. Relative - recent nasal surgery, coagulopathy, esophageal varices. EQUIPMENT: NG tube (16-18Fr for adults), Water-soluble lubricant, Glass of water with straw, Kidney dish, Syringe (50ml), Stethoscope, pH paper, Adhesive tape, Gloves.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 'content', 3, 'Procedure', 'TECHNIQUE: 1. Explain procedure, obtain consent. 2. Position patient sitting upright (45-90°). 3. Measure tube length: nose to ear to xiphisternum (NEX measurement), mark tube. 4. Lubricate tube tip. 5. Insert through nostril, advance along floor of nose. 6. When tube reaches pharynx, ask patient to swallow sips of water. 7. Advance during swallowing until mark reaches nostril. 8. Confirm position before use. 9. Secure with tape to nose. TIPS: Use larger nostril. If resistance, do not force. Flex neck slightly during swallowing phase.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 'content', 4, 'Confirmation of Position', 'CONFIRMATION METHODS: 1. pH testing (gold standard) - aspirate 0.5-1ml, test pH. Gastric pH <5.5 confirms position. 2. Auscultation (whoosh test) - inject 20ml air while listening over epigastrium. Unreliable, not recommended alone. 3. Chest X-ray - definitive but not routine. Required if pH testing unsuccessful. INCORRECT POSITION SIGNS: Respiratory distress, coughing, cyanosis, inability to aspirate, high pH (>5.5).'),
('a1100011-0000-0000-0000-000000000011'::uuid, 'content', 5, 'Complications', 'IMMEDIATE: Nasal trauma/epistaxis, Pharyngeal trauma, Misplacement in trachea, Aspiration, Laryngospasm. DELAYED: Nasal necrosis (pressure), Sinusitis, Otitis media, Esophageal ulceration, Electrolyte disturbances (prolonged suction), Aspiration pneumonia. PREVENTION: Correct technique, confirm position before use, regular position checks, secure fixation, monitor electrolytes.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 'key_points', 6, 'Key Points', '1. Measure nose to ear to xiphisternum (NEX). 2. pH testing is gold standard for confirmation. 3. Gastric pH should be <5.5. 4. Auscultation alone is unreliable. 5. Base of skull fracture is absolute contraindication. 6. Position patient upright at 45-90°. 7. Advance tube during swallowing. 8. Never force against resistance. 9. Check position before each use. 10. Monitor for complications.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('a1100011-0000-0000-0000-000000000011'::uuid, 1, 'Standard NG tube size for adults is:', '8-10 Fr', '10-12 Fr', '16-18 Fr', '24-26 Fr', '30-32 Fr', 'C', 'Adult NG tubes are typically 16-18 French.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 2, 'NEX measurement is:', 'Nose to ear to xiphisternum', 'Nose to epigastrium to xiphoid', 'Nares to ear to xiphoid', 'All are correct', 'None are correct', 'A', 'NEX = Nose to Ear to Xiphisternum.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 3, 'Gold standard for NG tube position confirmation:', 'Auscultation', 'pH testing', 'X-ray', 'Bubbling in water', 'Patient comfort', 'B', 'pH testing (gastric pH <5.5) is gold standard.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 4, 'Gastric aspirate pH should be:', '<5.5', '>7.0', '6.0-7.0', '>8.0', '5.5-6.5', 'A', 'Gastric pH <5.5 confirms correct position.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 5, 'Absolute contraindication to NG tube:', 'Hiatus hernia', 'Base of skull fracture', 'Gastric ulcer', 'Esophageal varices', 'Recent vomiting', 'B', 'Base of skull fracture risks intracranial placement.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 6, 'Patient position for NG insertion:', 'Supine flat', 'Left lateral', 'Upright 45-90°', 'Trendelenburg', 'Right lateral', 'C', 'Patient should be upright at 45-90 degrees.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 7, 'Tube should be advanced during:', 'Inspiration', 'Expiration', 'Swallowing', 'Breath holding', 'Coughing', 'C', 'Advance during swallowing to close airway.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 8, 'Auscultation test is:', 'Reliable alone', 'Gold standard', 'Unreliable, not recommended alone', 'Never used', 'Only for feeding tubes', 'C', 'Whoosh test is unreliable and not recommended alone.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 9, 'Salem sump tube has:', 'Single lumen', 'Double lumen', 'Triple lumen', 'No lumen', 'Multiple holes only', 'B', 'Salem sump has double lumen for suction.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 10, 'If resistance during insertion:', 'Force the tube', 'Withdraw and retry', 'Call senior', 'Increase lubrication only', 'Continue blindly', 'B', 'Never force - withdraw and retry or reassess.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 11, 'Lubricant used for NG tube:', 'Petroleum jelly', 'Water-soluble gel', 'Oil-based lubricant', 'No lubricant needed', 'Alcohol', 'B', 'Water-soluble lubricant (e.g., KY jelly) is used.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 12, 'Complication of prolonged NG suction:', 'Hyperkalemia', 'Metabolic alkalosis', 'Respiratory alkalosis', 'Metabolic acidosis', 'Hypernatremia', 'B', 'Loss of gastric acid causes metabolic alkalosis.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 13, 'Sign of tube in trachea:', 'Gurgling sound', 'Patient comfort', 'Coughing and distress', 'Easy aspiration', 'Low pH aspirate', 'C', 'Coughing, distress, and inability to speak indicate tracheal placement.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 14, 'Fine bore tubes are used for:', 'Gastric decompression', 'Enteral feeding', 'Gastric lavage', 'Bowel obstruction', 'Suction', 'B', 'Fine bore tubes are for feeding only.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 15, 'X-ray for NG position is:', 'Always required', 'Required if pH testing unsuccessful', 'Never needed', 'Only for feeding tubes', 'Contraindicated', 'B', 'X-ray is used when pH testing is unsuccessful.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 16, 'Ryle''s tube is:', 'Double lumen', 'Single lumen', 'Triple lumen', 'For feeding only', 'Made of rubber', 'B', 'Ryles tube is single lumen.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 17, 'When tube reaches pharynx:', 'Push quickly', 'Ask patient to swallow', 'Rotate tube', 'Withdraw immediately', 'Inflate balloon', 'B', 'Patient swallows to help tube pass to esophagus.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 18, 'Sinusitis from NG tube is:', 'Immediate complication', 'Delayed complication', 'Never occurs', 'Only with fine bore', 'Only with Salem sump', 'B', 'Sinusitis is a delayed complication.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 19, 'Nasal necrosis is prevented by:', 'Using larger tube', 'Proper fixation without tension', 'Using thicker tape', 'Inserting deeper', 'Frequent repositioning', 'B', 'Proper fixation prevents pressure necrosis.'),
('a1100011-0000-0000-0000-000000000011'::uuid, 20, 'Position should be checked:', 'Once on insertion', 'Before each feed/medication', 'Weekly', 'Only if symptomatic', 'Never after initial X-ray', 'B', 'Position should be confirmed before each use.');
