INSERT INTO article_sections (article_id, section_type, section_order, title, content) VALUES
('a1100010-0000-0000-0000-000000000010'::uuid, 'introduction', 1, 'Introduction', 'Mensuration refers to measurement of body parameters. Physical examination is systematic assessment of the body. Both are essential baseline assessments in surgery. Key measurements: Height, Weight, BMI, Vital signs, Body surface area. Physical exam follows: Inspection, Palpation, Percussion, Auscultation.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 'content', 2, 'Anthropometric Measurements', 'WEIGHT: Use calibrated scale, consistent timing (morning, fasting). Record in kg. IDEAL BODY WEIGHT: Males: 50kg + 2.3kg per inch over 5 feet. Females: 45.5kg + 2.3kg per inch over 5 feet. BMI = Weight(kg)/Height(m)². Classification: <18.5 Underweight, 18.5-24.9 Normal, 25-29.9 Overweight, 30-34.9 Obesity I, 35-39.9 Obesity II, >40 Obesity III. BODY SURFACE AREA (BSA): Mosteller formula: √(Height cm × Weight kg / 3600). Used for drug dosing, burn assessment, cardiac index.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 'content', 3, 'Vital Signs', 'TEMPERATURE: Normal 36.1-37.2°C (97-99°F). Fever >38°C. Sites: oral, axillary (+0.5°C), rectal (-0.5°C), tympanic. PULSE: Rate 60-100/min. Rhythm, Volume, Character. Sites: radial, carotid, femoral, dorsalis pedis. BLOOD PRESSURE: Normal <120/80 mmHg. Hypertension Stage 1: 130-139/80-89. Stage 2: ≥140/90. RESPIRATORY RATE: Normal 12-20/min. Tachypnea >20, Bradypnea <12. OXYGEN SATURATION: Normal >94% on room air.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 'content', 4, 'Physical Examination Technique', 'INSPECTION: Observe before touching. General appearance, posture, gait, skin color, obvious abnormalities. PALPATION: Light then deep. Temperature, tenderness, masses, organomegaly, pulses. Use pads of fingers. PERCUSSION: Direct or indirect. Resonant (lung), Dull (liver), Tympanic (bowel gas), Hyper-resonant (pneumothorax). AUSCULTATION: Use diaphragm for high-pitched, bell for low-pitched sounds. Heart sounds, breath sounds, bowel sounds, bruits.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 'content', 5, 'Regional Examination', 'ABDOMINAL EXAM: Inspection (distension, scars, masses), Auscultation (bowel sounds first), Percussion (ascites, organomegaly), Palpation (tenderness, guarding, rigidity, masses). RESPIRATORY EXAM: Tracheal position, Chest expansion, Vocal fremitus, Percussion note, Breath sounds. CARDIOVASCULAR: JVP, Apex beat, Heart sounds, Peripheral pulses, Edema. NEUROLOGICAL: Mental status, Cranial nerves, Motor, Sensory, Reflexes, Coordination.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 'key_points', 6, 'Key Points', '1. BMI = Weight(kg)/Height(m)². 2. Normal BMI 18.5-24.9. 3. Physical exam order: Inspection, Palpation, Percussion, Auscultation. 4. Abdominal exam: auscultate before palpation. 5. Use diaphragm for high-pitched sounds. 6. Normal pulse 60-100/min. 7. Normal BP <120/80. 8. Normal RR 12-20/min. 9. BSA used for drug dosing and burns. 10. Systematic approach essential.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('a1100010-0000-0000-0000-000000000010'::uuid, 1, 'BMI formula is:', 'Weight × Height²', 'Weight/Height²', 'Weight²/Height', 'Height/Weight²', 'Weight + Height', 'B', 'BMI = Weight(kg)/Height(m)².'),
('a1100010-0000-0000-0000-000000000010'::uuid, 2, 'Normal BMI range is:', '<18.5', '18.5-24.9', '25-29.9', '30-34.9', '>40', 'B', 'Normal BMI is 18.5-24.9.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 3, 'Obesity Class II BMI is:', '25-29.9', '30-34.9', '35-39.9', '>40', '20-24.9', 'C', 'Obesity Class II is BMI 35-39.9.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 4, 'Physical exam order is:', 'Palpation, Inspection, Percussion, Auscultation', 'Inspection, Auscultation, Palpation, Percussion', 'Inspection, Palpation, Percussion, Auscultation', 'Auscultation, Inspection, Palpation, Percussion', 'Any order', 'C', 'Standard order: Inspection, Palpation, Percussion, Auscultation.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 5, 'Exception to physical exam order is:', 'Respiratory exam', 'Abdominal exam', 'Cardiovascular exam', 'Neurological exam', 'Musculoskeletal exam', 'B', 'Abdominal exam: auscultate before palpation.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 6, 'Normal resting pulse rate is:', '40-60/min', '60-100/min', '100-120/min', '120-150/min', '80-140/min', 'B', 'Normal pulse 60-100 beats per minute.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 7, 'Normal blood pressure is:', '<100/60', '<120/80', '<140/90', '<160/100', '<180/110', 'B', 'Normal BP is <120/80 mmHg.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 8, 'Normal respiratory rate is:', '6-12/min', '12-20/min', '20-30/min', '30-40/min', '8-16/min', 'B', 'Normal RR 12-20 breaths per minute.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 9, 'BSA is used for:', 'Blood pressure calculation', 'Drug dosing and burns', 'Weight prediction', 'Height measurement', 'Pulse assessment', 'B', 'BSA used for drug dosing and burn assessment.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 10, 'Diaphragm of stethoscope is for:', 'Low-pitched sounds', 'High-pitched sounds', 'All sounds equally', 'Heart sounds only', 'Bowel sounds only', 'B', 'Diaphragm for high-pitched sounds.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 11, 'Bell of stethoscope is for:', 'High-pitched sounds', 'Low-pitched sounds', 'Breath sounds', 'Bowel sounds', 'Percussion', 'B', 'Bell for low-pitched sounds (e.g., S3, S4).'),
('a1100010-0000-0000-0000-000000000010'::uuid, 12, 'Tympanic percussion note heard over:', 'Liver', 'Lungs', 'Gas-filled bowel', 'Heart', 'Solid mass', 'C', 'Tympanic note over gas-filled bowel.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 13, 'Dull percussion note heard over:', 'Normal lung', 'Bowel', 'Liver', 'Pneumothorax', 'Emphysema', 'C', 'Dull note over solid organs like liver.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 14, 'Hyper-resonant note suggests:', 'Pleural effusion', 'Pneumothorax', 'Consolidation', 'Liver enlargement', 'Ascites', 'B', 'Hyper-resonance in pneumothorax or emphysema.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 15, 'Ideal body weight for male 5''10'' is:', '50 kg', '59.5 kg', '73 kg', '80 kg', '65 kg', 'C', '50 + (2.3 × 10) = 73 kg for male 5''10''.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 16, 'Axillary temperature compared to oral:', '0.5°C lower', '0.5°C higher', 'Same', '1°C higher', '1°C lower', 'A', 'Axillary temperature is about 0.5°C lower than oral.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 17, 'Stage 2 hypertension BP is:', '>120/80', '>130/85', '≥140/90', '>160/100', '>180/110', 'C', 'Stage 2 hypertension is ≥140/90 mmHg.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 18, 'Normal oxygen saturation on room air:', '>80%', '>85%', '>90%', '>94%', '100%', 'D', 'Normal SpO2 >94% on room air.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 19, 'Palpation uses:', 'Fingertips', 'Finger pads', 'Palm only', 'Knuckles', 'Dorsum of hand', 'B', 'Use finger pads for palpation.'),
('a1100010-0000-0000-0000-000000000010'::uuid, 20, 'Morbid obesity BMI is:', '>30', '>35', '>40', '>25', '>45', 'C', 'Morbid obesity (Class III) is BMI >40.');
