INSERT INTO article_sections (article_id, section_type, section_order, title, content) VALUES
('d0000037-0000-0000-0000-000000000037'::uuid, 'introduction', 1, 'Introduction to Benign Breast Diseases', 'Benign breast conditions are extremely common, affecting up to 50% of women during their lifetime. They encompass a spectrum of disorders including fibrocystic changes, fibroadenomas, breast cysts, mastitis, and nipple discharge. Understanding these conditions is essential to differentiate from malignancy and provide appropriate management. ANDI (Aberrations of Normal Development and Involution) classification provides a framework understanding benign breast disease as deviations from normal breast physiology.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 'content', 2, 'Classification and Common Conditions', 'ANDI CLASSIFICATION: Normal processes become aberrations and may progress to disease states. Development phase (15-25 years): fibroadenoma. Cyclical changes (25-40 years): mastalgia, nodularity. Involution (35-55 years): cysts, duct ectasia, sclerosing adenosis.
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000037-0000-0000-0000-000000000037'::uuid, 1, 'Most common breast lump in women under 30 is:', 'Breast cyst', 'Fibroadenoma', 'Breast cancer', 'Fat necrosis', 'Lipoma', 'B', 'Fibroadenoma is the most common breast lump in women under 30 years of age.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 2, 'Characteristic feature of fibroadenoma is:', 'Fixed to skin', 'Highly mobile (breast mouse)', 'Nipple discharge', 'Skin dimpling', 'Axillary lymphadenopathy', 'B', 'Fibroadenoma is characteristically highly mobile, earning the nickname breast mouse.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 3, 'After cyst aspiration, no further action is needed if:', 'Fluid is bloody', 'Lump remains', 'Fluid is clear, lump disappears, no residual mass', 'Patient is young', 'Cyst is large', 'C', 'Successful cyst aspiration criteria: clear fluid, complete disappearance of lump, no residual mass.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 4, 'Lactational mastitis is most commonly caused by:', 'E. coli', 'Staphylococcus aureus', 'Streptococcus', 'Pseudomonas', 'Candida', 'B', 'Staphylococcus aureus is the most common organism in lactational mastitis.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 5, 'Management of lactational mastitis includes:', 'Stop breastfeeding immediately', 'Continue breastfeeding and give antibiotics', 'Immediate surgery', 'Observation only', 'Warm compresses only', 'B', 'Continue breastfeeding helps drain the breast. Flucloxacillin is the antibiotic of choice.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 6, 'Periductal mastitis is associated with:', 'Pregnancy', 'Smoking', 'Diabetes', 'Old age', 'Obesity', 'B', 'Periductal mastitis is strongly associated with smoking and nipple piercing.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 7, 'Bloody nipple discharge in a single duct suggests:', 'Duct ectasia', 'Fibrocystic change', 'Intraductal papilloma or carcinoma', 'Galactorrhea', 'Normal physiology', 'C', 'Unilateral single-duct bloody discharge requires investigation for papilloma or carcinoma.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 8, 'Triple assessment includes all EXCEPT:', 'Clinical examination', 'Imaging', 'Blood tests', 'Tissue diagnosis', 'Core biopsy', 'C', 'Triple assessment consists of clinical examination, imaging, and tissue diagnosis. Blood tests are not part of triple assessment.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 9, 'First imaging modality in women under 35 is:', 'Mammogram', 'Ultrasound', 'MRI', 'CT scan', 'PET scan', 'B', 'Ultrasound is preferred in women under 35 due to dense breast tissue making mammography less effective.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 10, 'C5 on cytology grading means:', 'Inadequate sample', 'Benign', 'Atypical', 'Suspicious', 'Malignant', 'E', 'C5 indicates malignant cells on fine needle aspiration cytology.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 11, 'Fat necrosis typically has a history of:', 'Infection', 'Trauma or surgery', 'Pregnancy', 'Smoking', 'Diabetes', 'B', 'Fat necrosis usually follows trauma or previous breast surgery.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 12, 'Phyllodes tumor management is:', 'Observation', 'Wide local excision', 'Lumpectomy only', 'Antibiotics', 'Aspiration', 'B', 'Phyllodes tumor requires wide local excision due to risk of malignancy and local recurrence.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 13, 'Milky nipple discharge is associated with:', 'Breast cancer', 'Prolactinoma', 'Duct ectasia', 'Papilloma', 'Fat necrosis', 'B', 'Galactorrhea (milky discharge) suggests hyperprolactinemia, often from pituitary adenoma.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 14, 'ANDI stands for:', 'Abnormal Nipple Duct Inflammation', 'Aberrations of Normal Development and Involution', 'Atypical Nodular Ductal Involvement', 'Advanced Neoplastic Disease Index', 'Acute Non-specific Ductal Infection', 'B', 'ANDI - Aberrations of Normal Development and Involution - framework for benign breast disease.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 15, 'Green-brown nipple discharge suggests:', 'Carcinoma', 'Duct ectasia', 'Papilloma', 'Mastitis', 'Galactorrhea', 'B', 'Green or brown cheesy discharge is characteristic of mammary duct ectasia.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 16, 'Non-lactational breast abscess location is typically:', 'Upper outer quadrant', 'Periareolar', 'Axillary tail', 'Lower inner quadrant', 'Retromammary', 'B', 'Non-lactational abscesses are typically periareolar, related to periductal mastitis.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 17, 'Breast cysts are most common in which age group?', '15-25 years', '25-35 years', '35-50 years', 'Over 60 years', 'Under 15 years', 'C', 'Breast cysts are most common in the 35-50 age group as part of involution.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 18, 'Core biopsy result B2 indicates:', 'Inadequate sample', 'Benign', 'Atypical', 'Suspicious', 'Malignant', 'B', 'B2 on core biopsy grading indicates a benign lesion.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 19, 'High-risk lesions requiring excision include all EXCEPT:', 'Atypical ductal hyperplasia', 'Lobular carcinoma in situ', 'Simple fibroadenoma', 'Radial scar', 'Papillary lesions', 'C', 'Simple fibroadenoma is not a high-risk lesion. ADH, LCIS, radial scar, and papillary lesions require excision.'),
('d0000037-0000-0000-0000-000000000037'::uuid, 20, 'In non-lactational breast abscess, one should always exclude:', 'Pregnancy', 'Inflammatory carcinoma', 'Tuberculosis', 'Diabetes', 'Lymphoma', 'B', 'Always exclude inflammatory breast carcinoma in non-lactational abscess, especially if not responding to treatment.');
