INSERT INTO article_sections (article_id, section_type, section_order, title, content) VALUES
('d0000043-0000-0000-0000-000000000043'::uuid, 'introduction', 1, 'Introduction to Dysphagia', 'Dysphagia is difficulty swallowing and is a symptom requiring thorough investigation to exclude malignancy. It may be oropharyngeal (transfer dysphagia) or esophageal. Esophageal dysphagia may be due to mechanical obstruction (stricture, cancer) or motility disorders (achalasia, diffuse esophageal spasm). Progressive dysphagia to solids suggests mechanical obstruction; dysphagia to both solids and liquids from onset suggests motility disorder. Alarm symptoms requiring urgent investigation include weight loss, anemia, and age over 55.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 'content', 2, 'Causes and Investigation of Dysphagia', 'OROPHARYNGEAL DYSPHAGIA: Neurological (stroke, Parkinson, MND, myasthenia gravis), muscular (polymyositis, muscular dystrophy), structural (pharyngeal pouch, Zenker diverticulum). Presents with difficulty initiating swallow, nasal regurgitation, coughing, aspiration.
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000043-0000-0000-0000-000000000043'::uuid, 1, 'Progressive dysphagia to solids only suggests:', 'Motility disorder', 'Mechanical obstruction', 'Functional dysphagia', 'Oropharyngeal dysphagia', 'Psychological cause', 'B', 'Progressive dysphagia to solids then liquids suggests progressive mechanical obstruction like stricture or cancer.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 2, 'Dysphagia to both solids and liquids from onset suggests:', 'Cancer', 'Peptic stricture', 'Motility disorder', 'Esophageal web', 'Foreign body', 'C', 'Dysphagia to both solids and liquids from the beginning suggests esophageal motility disorder.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 3, 'Bird beak appearance on barium swallow is seen in:', 'Esophageal cancer', 'Achalasia', 'Diffuse esophageal spasm', 'Peptic stricture', 'Pharyngeal pouch', 'B', 'Bird beak or rat tail appearance is characteristic of achalasia due to failure of LES relaxation.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 4, 'Achalasia is diagnosed definitively by:', 'Barium swallow', 'Endoscopy', 'Esophageal manometry', 'CT scan', 'pH study', 'C', 'Esophageal manometry is definitive, showing absent peristalsis and incomplete LES relaxation.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 5, 'Barrett esophagus is:', 'Squamous cell carcinoma', 'Intestinal metaplasia of esophageal epithelium', 'Inflammatory stricture', 'Motility disorder', 'Hiatal hernia', 'B', 'Barrett esophagus is intestinal (columnar) metaplasia replacing normal squamous epithelium, premalignant for adenocarcinoma.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 6, 'Annual risk of adenocarcinoma in Barrett esophagus is approximately:', '0.1%', '0.5%', '5%', '10%', '25%', 'B', 'Barrett esophagus carries approximately 0.5% annual risk of progression to adenocarcinoma.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 7, 'Most common type of hiatal hernia is:', 'Sliding (Type I)', 'Paraesophageal (Type II)', 'Mixed (Type III)', 'Type IV', 'Rolling', 'A', 'Sliding (Type I) hiatal hernia accounts for about 95% of all hiatal hernias.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 8, 'Laparoscopic Nissen fundoplication is a:', '180-degree anterior wrap', '270-degree posterior wrap', '360-degree complete wrap', '90-degree wrap', 'Partial anterior and posterior wrap', 'C', 'Nissen fundoplication is a 360-degree (complete) wrap of the gastric fundus around the esophagus.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 9, 'Corkscrew esophagus on barium swallow indicates:', 'Achalasia', 'Diffuse esophageal spasm', 'Cancer', 'Stricture', 'Barrett esophagus', 'B', 'Corkscrew or rosary bead appearance is characteristic of diffuse esophageal spasm.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 10, 'Pharyngeal pouch (Zenker diverticulum) occurs through:', 'Esophageal muscle wall', 'Killian dehiscence', 'Lower esophageal sphincter', 'Gastric cardia', 'Cricoid cartilage', 'B', 'Zenker diverticulum is a pulsion diverticulum through Killian dehiscence in the posterior pharynx.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 11, 'Plummer-Vinson syndrome consists of:', 'Dysphagia, iron deficiency, esophageal web', 'Achalasia and stricture', 'GERD and Barrett', 'Cancer and weight loss', 'Motility disorder and chest pain', 'A', 'Plummer-Vinson (Patterson-Kelly) syndrome: dysphagia, iron deficiency anemia, and upper esophageal web.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 12, 'Boerhaave syndrome is:', 'Spontaneous esophageal perforation', 'Achalasia', 'Esophageal cancer', 'Stricture', 'Motility disorder', 'A', 'Boerhaave syndrome is spontaneous (postemetic) rupture of esophagus, usually left posterolateral distal esophagus.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 13, 'Mackler triad includes:', 'Fever, jaundice, RUQ pain', 'Vomiting, chest pain, subcutaneous emphysema', 'Dysphagia, weight loss, anemia', 'Heartburn, regurgitation, dysphagia', 'Cough, fever, dyspnea', 'B', 'Mackler triad for esophageal perforation: vomiting, lower thoracic pain, subcutaneous emphysema.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 14, 'Most common cause of esophageal perforation is:', 'Boerhaave syndrome', 'Iatrogenic (endoscopy)', 'Foreign body', 'Trauma', 'Malignancy', 'B', 'Iatrogenic perforation (during endoscopy) is the most common cause of esophageal perforation.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 15, 'Surgical treatment for achalasia is:', 'Esophagectomy', 'Heller myotomy', 'Nissen fundoplication', 'Stricturoplasty', 'Gastric bypass', 'B', 'Laparoscopic Heller myotomy (cardiomyotomy) with partial fundoplication is surgical treatment for achalasia.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 16, 'Paraesophageal hernia is concerning because of risk of:', 'Acid reflux', 'Gastric volvulus and strangulation', 'Esophageal cancer', 'Motility disorder', 'Bleeding', 'B', 'Type II (paraesophageal) hernias risk gastric volvulus, strangulation, and incarceration.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 17, 'First-line medical treatment for GERD is:', 'H2 blockers', 'Proton pump inhibitors', 'Antacids', 'Prokinetics', 'Sucralfate', 'B', 'Proton pump inhibitors (PPIs) are the most effective first-line medical treatment for GERD.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 18, 'Schatzki ring is located at:', 'Upper esophagus', 'Mid-esophagus', 'Gastroesophageal junction', 'Pylorus', 'Pharynx', 'C', 'Schatzki ring is a mucosal ring at the gastroesophageal junction causing intermittent dysphagia to solids.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 19, 'POEM procedure for achalasia stands for:', 'Partial oral endoscopic myotomy', 'Peroral endoscopic myotomy', 'Primary open esophageal myotomy', 'Posterior oral esophageal myectomy', 'Pneumatic oral endoscopic maneuver', 'B', 'POEM - Peroral Endoscopic Myotomy is a newer endoscopic technique for treating achalasia.'),
('d0000043-0000-0000-0000-000000000043'::uuid, 20, 'Los Angeles classification grades:', 'Barrett esophagus', 'Reflux esophagitis severity', 'Achalasia', 'Esophageal cancer staging', 'Hiatal hernia size', 'B', 'Los Angeles classification (A-D) grades severity of erosive reflux esophagitis on endoscopy.');
