INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000008-0000-0000-0000-000000000008'::uuid, 6, 'Most common cause of typhoid intestinal perforation is:', 'Ileal perforation', 'Jejunal perforation', 'Colonic perforation', 'Gastric perforation', 'Duodenal perforation', 'A', 'Terminal ileum is the most common site of typhoid perforation due to Peyer patch necrosis.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 7, 'Widal test becomes positive in which week of typhoid fever?', 'First week', 'Second week', 'Fourth week', 'Sixth week', 'Immediately', 'B', 'Widal test becomes positive by end of first week or beginning of second week of illness.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 8, 'Gold standard for typhoid diagnosis is:', 'Widal test', 'Blood culture', 'Stool culture', 'Urine culture', 'PCR', 'B', 'Blood culture is the gold standard for typhoid fever diagnosis.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 9, 'Carrier state in typhoid is defined as:', 'Excretion for 1 week', 'Excretion for 1 month', 'Excretion for more than 1 year', 'No symptoms', 'Positive Widal', 'C', 'Chronic carrier state is defined as excretion of Salmonella typhi for more than 1 year.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 10, 'Most common organism in non-typhoid Salmonella gastroenteritis is:', 'S. typhi', 'S. paratyphi', 'S. enteritidis', 'S. choleraesuis', 'S. dublin', 'C', 'S. enteritidis and S. typhimurium are the most common non-typhoid Salmonella species causing gastroenteritis.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 11, 'Rose spots in typhoid are found on:', 'Face', 'Trunk and abdomen', 'Arms', 'Legs', 'Palms', 'B', 'Rose spots are salmon-colored maculopapular lesions found on trunk and abdomen in 30% of typhoid cases.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 12, 'Treatment of choice for typhoid fever is:', 'Metronidazole', 'Ciprofloxacin or Ceftriaxone', 'Ampicillin only', 'Erythromycin', 'Gentamicin', 'B', 'Fluoroquinolones (ciprofloxacin) or ceftriaxone are first-line treatment for typhoid fever.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 13, 'Typhoid perforation typically occurs in which week?', 'First week', 'Second week', 'Third week', 'Fifth week', 'Eighth week', 'C', 'Intestinal perforation typically occurs in the third week of untreated typhoid fever.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 14, 'Surgery for typhoid perforation involves:', 'Conservative management', 'Primary closure or resection', 'Observation only', 'Antibiotics only', 'Delayed surgery always', 'B', 'Surgical management includes simple closure, wedge excision, or segmental resection with or without anastomosis.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 15, 'Gallbladder is significant in typhoid carriers because:', 'It causes cholecystitis', 'Organisms persist in bile', 'It is always removed', 'It causes jaundice', 'No significance', 'B', 'Gallbladder is a reservoir for chronic carriage as organisms persist in bile and gallstones.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 16, 'Relative bradycardia in typhoid means:', 'Heart rate increases with fever', 'Heart rate lower than expected for fever', 'No change in heart rate', 'Tachycardia', 'Arrhythmia', 'B', 'Relative bradycardia (Faget sign) means heart rate is lower than expected for the degree of fever.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 17, 'Multiple perforations in typhoid are:', 'Rare', 'Common (30-50%)', 'Never occur', 'Only in children', 'Only in immunocompromised', 'B', 'Multiple perforations are common in typhoid (30-50% of surgical cases).'),
('d0000008-0000-0000-0000-000000000008'::uuid, 18, 'Post-operative complication specific to typhoid surgery:', 'Wound infection', 'Fecal fistula', 'Bleeding', 'Hernia', 'All of the above', 'E', 'Complications include wound infection, fecal fistula, further perforations, sepsis, and incisional hernia.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 19, 'Typhoid vaccine provides protection for:', '6 months', '1-2 years', '3 years', 'Lifetime', '10 years', 'C', 'Typhoid vaccines provide protection for approximately 3 years.'),
('d0000008-0000-0000-0000-000000000008'::uuid, 20, 'Salmonella osteomyelitis is associated with:', 'Normal individuals', 'Sickle cell disease', 'Diabetes only', 'Elderly only', 'Never occurs', 'B', 'Salmonella osteomyelitis is classically associated with sickle cell disease.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000009-0000-0000-0000-000000000009'::uuid, 8, 'Water lily sign on imaging is characteristic of:', 'Ascariasis', 'Hydatid cyst', 'Amebic abscess', 'Filariasis', 'Schistosomiasis', 'B', 'Water lily sign (collapsed membranes floating in cyst fluid) is characteristic of ruptured hydatid cyst.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 9, 'Treatment of choice for intestinal ascariasis is:', 'Surgery', 'Albendazole or Mebendazole', 'Praziquantel', 'Metronidazole', 'Ivermectin', 'B', 'Albendazole or mebendazole are the treatment of choice for intestinal ascariasis.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 10, 'Amebic liver abscess is most common in which lobe?', 'Left lobe', 'Right lobe', 'Caudate lobe', 'Both lobes equally', 'Quadrate lobe', 'B', 'Right lobe of liver is most commonly affected in amebic liver abscess.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 11, 'Anchovy sauce pus is characteristic of:', 'Pyogenic abscess', 'Amebic liver abscess', 'Hydatid cyst', 'Tuberculous abscess', 'Fungal abscess', 'B', 'Anchovy sauce (chocolate/reddish-brown) pus is characteristic of amebic liver abscess.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 12, 'Drug of choice for amebic liver abscess is:', 'Albendazole', 'Metronidazole', 'Praziquantel', 'Chloroquine', 'Mebendazole', 'B', 'Metronidazole is the drug of choice for invasive amebiasis including liver abscess.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 13, 'Daughter cysts are seen in:', 'Amebic abscess', 'Hydatid cyst', 'Pyogenic abscess', 'Ascariasis', 'Filariasis', 'B', 'Daughter cysts are characteristic of hydatid disease (Echinococcus).'),
('d0000009-0000-0000-0000-000000000009'::uuid, 14, 'Casoni test is used for diagnosis of:', 'Amebiasis', 'Hydatid disease', 'Ascariasis', 'Filariasis', 'Schistosomiasis', 'B', 'Casoni intradermal test was historically used for hydatid disease diagnosis.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 15, 'Spillage of hydatid cyst contents can cause:', 'No reaction', 'Anaphylaxis', 'Mild fever only', 'Localized infection', 'Delayed reaction only', 'B', 'Spillage of hydatid cyst contents can cause severe anaphylaxis due to antigenic nature of contents.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 16, 'Scolicidal agent used during hydatid surgery:', 'Normal saline', 'Hypertonic saline 20%', 'Dextrose', 'Plain water', 'Antibiotics', 'B', 'Hypertonic saline (20%) or cetrimide is used as scolicidal agent during hydatid surgery.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 17, 'Biliary ascariasis is treated by:', 'Emergency surgery', 'ERCP and extraction', 'Conservative management', 'Chemotherapy only', 'Radiation', 'B', 'ERCP with worm extraction is the treatment of choice for biliary ascariasis.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 18, 'Intermediate host of Echinococcus granulosus is:', 'Dog', 'Sheep/cattle/humans', 'Snail', 'Mosquito', 'Pig', 'B', 'Sheep, cattle, and humans are intermediate hosts; dogs are definitive hosts.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 19, 'Intestinal obstruction due to Ascaris is managed initially by:', 'Emergency surgery', 'Conservative management with antihelminthics', 'Observation only', 'Endoscopy', 'Radiation', 'B', 'Conservative management with nasogastric decompression and antihelminthics is first-line; surgery if fails.'),
('d0000009-0000-0000-0000-000000000009'::uuid, 20, 'PAIR technique for hydatid cyst stands for:', 'Puncture, Aspiration, Injection, Re-aspiration', 'Partial resection', 'Primary anastomosis', 'Pericystectomy', 'Prevention and intervention', 'A', 'PAIR: Puncture, Aspiration, Injection of scolicidal, Re-aspiration - minimally invasive treatment.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000010-0000-0000-0000-000000000010'::uuid, 9, 'Most common form of intestinal tuberculosis is:', 'Hyperplastic', 'Ulcerative', 'Ulcero-hyperplastic', 'Stricturous', 'Nodular', 'C', 'Ulcero-hyperplastic is the most common form of intestinal tuberculosis.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 10, 'Transverse ulcers in intestinal TB occur due to:', 'Random spread', 'Spread along lymphatics', 'Hematogenous spread', 'Direct invasion', 'Contiguous spread', 'B', 'Transverse ulcers occur because TB spreads along lymphatics which run transversely in the intestine.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 11, 'Ascites in abdominal TB is typically:', 'Transudative', 'Exudative with high protein', 'Hemorrhagic', 'Chylous', 'Clear', 'B', 'TB ascites is exudative with high protein (>3g/dL), high SAAG, and lymphocyte predominance.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 12, 'ADA (Adenosine Deaminase) is elevated in:', 'Pyogenic peritonitis', 'TB peritonitis', 'Malignant ascites', 'Cirrhotic ascites', 'Pancreatic ascites', 'B', 'Elevated ADA (>30-40 U/L) in ascitic fluid is suggestive of tuberculous peritonitis.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 13, 'Doughy abdomen is felt in which type of TB peritonitis?', 'Wet type', 'Dry type', 'Encysted type', 'All types', 'Never in TB', 'B', 'Doughy abdomen (plastic feel) is characteristic of dry or plastic type of TB peritonitis.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 14, 'Duration of ATT for abdominal tuberculosis is:', '2 months', '4 months', '6 months', '9-12 months', '2 years', 'D', 'Abdominal TB requires extended treatment of 9-12 months ATT.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 15, 'Most common surgical complication of intestinal TB is:', 'Perforation', 'Stricture causing obstruction', 'Fistula', 'Hemorrhage', 'Malignant transformation', 'B', 'Stricture causing intestinal obstruction is the most common surgical complication.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 16, 'Surgery in intestinal TB is indicated for:', 'All cases', 'Complications only', 'Diagnostic uncertainty', 'Both B and C', 'Never indicated', 'D', 'Surgery is indicated for complications (obstruction, perforation, fistula) and diagnostic uncertainty.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 17, 'Stricturoplasty in TB is preferred when:', 'Single stricture', 'Multiple short strictures', 'Long strictures', 'Active disease', 'Always preferred', 'B', 'Stricturoplasty is preferred for multiple short strictures to preserve bowel length.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 18, 'Cocoon abdomen is seen in:', 'Intestinal TB', 'Abdominal cocoon/sclerosing encapsulating peritonitis', 'Liver TB', 'Splenic TB', 'Renal TB', 'B', 'Cocoon abdomen (sclerosing encapsulating peritonitis) is a rare complication of TB peritonitis.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 19, 'Omental cake is seen in:', 'TB peritonitis', 'Ovarian malignancy', 'Both', 'Neither', 'Pyogenic peritonitis', 'C', 'Omental cake (thickened omentum) is seen in both TB peritonitis and peritoneal carcinomatosis.'),
('d0000010-0000-0000-0000-000000000010'::uuid, 20, 'Paradoxical response to ATT in abdominal TB means:', 'No response', 'Clinical worsening despite treatment', 'Rapid improvement', 'Drug resistance', 'Side effects', 'B', 'Paradoxical response is clinical/radiological worsening despite effective ATT due to immune reconstitution.');
