INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000011-0000-0000-0000-000000000011'::uuid, 6, 'Most common cause of preventable death in trauma is:', 'Head injury', 'Airway obstruction', 'Hemorrhage', 'Sepsis', 'Pulmonary embolism', 'C', 'Hemorrhage is the most common cause of preventable death in trauma patients.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 7, 'Golden hour in trauma refers to:', 'First hour after injury for definitive care', 'Time to CT scan', 'Time to surgery', 'Pre-hospital time', 'ICU admission time', 'A', 'The golden hour emphasizes that definitive care within the first hour improves survival.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 8, 'Most reliable indicator of adequate resuscitation is:', 'Blood pressure', 'Heart rate', 'Urine output', 'Capillary refill', 'Skin color', 'C', 'Urine output (0.5-1 mL/kg/hr) is the most reliable indicator of adequate tissue perfusion and resuscitation.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 9, 'Damage control surgery involves:', 'Complete definitive repair', 'Abbreviated surgery to control hemorrhage and contamination', 'No surgery', 'Delayed surgery only', 'Elective surgery', 'B', 'Damage control surgery involves abbreviated operations to control bleeding and contamination, followed by ICU resuscitation.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 10, 'The lethal triad in trauma includes:', 'Fever, tachycardia, shock', 'Hypothermia, acidosis, coagulopathy', 'Hypoxia, hypercarbia, acidosis', 'Hemorrhage, infection, organ failure', 'Shock, sepsis, ARDS', 'B', 'The lethal triad is hypothermia, acidosis, and coagulopathy - associated with high mortality.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 11, 'Massive transfusion protocol ratio of RBC:FFP:Platelets is:', '1:1:1', '3:1:1', '6:1:1', '1:3:1', '10:1:1', 'A', 'Massive transfusion protocol uses 1:1:1 ratio of RBC:FFP:Platelets to prevent coagulopathy.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 12, 'Permissive hypotension targets SBP of:', '60 mmHg', '80-90 mmHg', '120 mmHg', '140 mmHg', '100 mmHg', 'B', 'Permissive hypotension targets SBP 80-90 mmHg until surgical control of bleeding is achieved.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 13, 'Primary survey in trauma follows:', 'ABCDE approach', 'Head to toe examination', 'Laboratory assessment', 'Imaging first', 'History taking', 'A', 'Primary survey follows ABCDE: Airway, Breathing, Circulation, Disability, Exposure.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 14, 'Focused Assessment with Sonography for Trauma (FAST) examines:', '4 areas for free fluid', 'Solid organ injury', 'Vascular injury', 'Bone fractures', 'Brain injury', 'A', 'FAST examines 4 areas: pericardium, hepatorenal, splenorenal, and pelvis for free fluid.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 15, 'E-FAST additionally examines:', 'Extremities', 'Pleural spaces for hemothorax/pneumothorax', 'Eyes', 'Ears', 'Esophagus', 'B', 'Extended FAST (E-FAST) includes bilateral pleural assessment for hemothorax/pneumothorax.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 16, 'Indication for emergency thoracotomy includes:', 'All chest trauma', 'Penetrating trauma with loss of vital signs', 'Stable hemothorax', 'Rib fractures', 'Minor chest injury', 'B', 'Emergency thoracotomy is indicated for penetrating chest trauma with recent loss of vital signs.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 17, 'Tranexamic acid in trauma should be given within:', '1 hour', '3 hours', '6 hours', '12 hours', 'Any time', 'B', 'Tranexamic acid should be given within 3 hours of injury (ideally within 1 hour) for maximum benefit.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 18, 'Injury Severity Score (ISS) is based on:', 'GCS only', 'AIS of 3 body regions', 'Vital signs', 'Laboratory values', 'Mechanism only', 'B', 'ISS is sum of squares of AIS (Abbreviated Injury Scale) of 3 most severely injured body regions.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 19, 'Revised Trauma Score includes:', 'ISS components', 'GCS, RR, SBP', 'Age and mechanism', 'Laboratory values', 'Imaging findings', 'B', 'Revised Trauma Score includes GCS, Respiratory Rate, and Systolic Blood Pressure.'),
('d0000011-0000-0000-0000-000000000011'::uuid, 20, 'Secondary survey is performed:', 'Before primary survey', 'After primary survey and resuscitation', 'Only in stable patients', 'In the OR only', 'Next day', 'B', 'Secondary survey (head-to-toe examination) is performed after primary survey and initial resuscitation.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000012-0000-0000-0000-000000000012'::uuid, 6, 'First priority in ATLS is:', 'Circulation', 'Breathing', 'Airway with C-spine protection', 'Disability', 'Exposure', 'C', 'Airway with cervical spine protection is the first priority in ATLS primary survey.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 7, 'Class III hemorrhage involves blood loss of:', 'Up to 15%', '15-30%', '30-40%', 'Greater than 40%', '5-10%', 'C', 'Class III hemorrhage involves 30-40% blood loss with tachycardia, hypotension, and confusion.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 8, 'Definitive airway in ATLS means:', 'Oxygen mask', 'Nasal cannula', 'Cuffed tube in trachea', 'LMA', 'Bag-valve mask', 'C', 'Definitive airway is a cuffed tube in the trachea (endotracheal or surgical airway).'),
('d0000012-0000-0000-0000-000000000012'::uuid, 9, 'Needle thoracostomy for tension pneumothorax is performed at:', '2nd ICS midclavicular line', '5th ICS midaxillary line', '4th ICS anterior axillary', 'Either A or B', 'Any intercostal space', 'D', 'Needle decompression can be performed at 2nd ICS midclavicular or 5th ICS midaxillary line.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 10, 'Initial fluid bolus in adult trauma is:', '500 mL', '1 liter', '2 liters crystalloid', '3 liters', '500 mL colloid', 'C', 'Initial fluid resuscitation is 1-2 liters of warmed crystalloid (lactated Ringers or normal saline).'),
('d0000012-0000-0000-0000-000000000012'::uuid, 11, 'GCS of 8 or less indicates:', 'Minor head injury', 'Moderate head injury', 'Severe head injury requiring intubation', 'Normal', 'Psychiatric issue', 'C', 'GCS 8 or less indicates severe head injury and need for definitive airway protection.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 12, 'Disability assessment in ATLS includes:', 'AVPU or GCS', 'Pupil examination', 'Lateralizing signs', 'All of the above', 'None of the above', 'D', 'Disability includes GCS/AVPU, pupil size and reactivity, and lateralizing signs.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 13, 'In C-spine immobilization, the cervical collar should be:', 'Removed immediately', 'Maintained until clinical and radiological clearance', 'Only for children', 'Optional', 'Used only if symptomatic', 'B', 'C-spine immobilization maintained until clinical and radiological clearance in high-risk patients.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 14, 'Exposure in ATLS means:', 'Covering the patient', 'Completely undressing patient while preventing hypothermia', 'X-ray exposure', 'Wound exposure', 'Light exposure', 'B', 'Exposure involves completely undressing the patient while preventing hypothermia with warm blankets.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 15, 'Signs of tension pneumothorax include all EXCEPT:', 'Tracheal deviation', 'Hypotension', 'Bilateral breath sounds', 'Distended neck veins', 'Respiratory distress', 'C', 'Tension pneumothorax has absent breath sounds on affected side, not bilateral.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 16, 'Indication for surgical airway includes:', 'Unable to intubate, unable to oxygenate', 'Facial trauma preventing intubation', 'Laryngeal fracture', 'All of the above', 'None of the above', 'D', 'Surgical airway is indicated when cannot intubate/cannot oxygenate, severe facial trauma, or laryngeal injury.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 17, 'Beck triad of cardiac tamponade includes:', 'Hypotension, JVD, muffled heart sounds', 'Tachycardia, fever, chest pain', 'Bradycardia, hypertension, irregular respiration', 'Dyspnea, cyanosis, stridor', 'Shock, pallor, diaphoresis', 'A', 'Beck triad: hypotension, distended neck veins (JVD), and muffled heart sounds.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 18, 'Pericardiocentesis is performed:', 'At apex', 'Subxiphoid approach', 'Through 5th intercostal space', 'Posterior approach', 'Any location', 'B', 'Emergency pericardiocentesis is performed via subxiphoid approach.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 19, 'Flail chest is defined as:', 'Single rib fracture', 'Two or more ribs fractured in two or more places', 'Sternal fracture', 'Clavicle fracture', 'Scapula fracture', 'B', 'Flail chest: segment of chest wall with 2+ ribs fractured in 2+ places, moving paradoxically.'),
('d0000012-0000-0000-0000-000000000012'::uuid, 20, 'Transfer to higher level of care is indicated when:', 'Local resources adequate', 'Injuries exceed local capability', 'Patient stable', 'Family request only', 'Never indicated', 'B', 'Transfer is indicated when patient needs exceed local hospital capability.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000013-0000-0000-0000-000000000013'::uuid, 6, 'Most commonly injured solid organ in blunt abdominal trauma is:', 'Liver', 'Spleen', 'Kidney', 'Pancreas', 'Adrenal', 'B', 'Spleen is the most commonly injured solid organ in blunt abdominal trauma.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 7, 'Most commonly injured organ in penetrating abdominal trauma is:', 'Spleen', 'Liver', 'Small bowel', 'Colon', 'Stomach', 'C', 'Small bowel is most commonly injured in penetrating abdominal trauma due to its size and location.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 8, 'Diagnostic peritoneal lavage (DPL) is positive if RBC count is:', 'Greater than 10,000/mm3', 'Greater than 100,000/mm3', 'Greater than 500/mm3', 'Greater than 1,000,000/mm3', 'Any blood', 'B', 'DPL is positive if RBC >100,000/mm3 in blunt trauma or >10,000/mm3 in penetrating trauma.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 9, 'Kehr sign indicates:', 'Liver injury', 'Splenic injury with diaphragm irritation', 'Bowel injury', 'Kidney injury', 'Bladder injury', 'B', 'Kehr sign is referred left shoulder pain from diaphragmatic irritation by splenic injury/blood.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 10, 'Non-operative management of splenic injury requires:', 'Hemodynamic stability', 'Low grade injury (I-III)', 'Ability to monitor closely', 'All of the above', 'Age less than 40 only', 'D', 'NOM requires hemodynamic stability, low-grade injury, no other indications for laparotomy, and monitoring capability.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 11, 'Seat belt sign is associated with injury to:', 'Solid organs only', 'Lumbar spine and hollow viscus', 'Chest only', 'Head only', 'No specific injury', 'B', 'Seat belt sign (bruising) is associated with lumbar spine (Chance fracture) and hollow viscus injury.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 12, 'Indication for mandatory laparotomy in penetrating trauma:', 'Evisceration', 'Hemodynamic instability', 'Peritonitis', 'All of the above', 'None - all can be observed', 'D', 'Mandatory laparotomy for: evisceration, peritonitis, hemodynamic instability, impalement.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 13, 'CT grading of liver injury Grade IV includes:', 'Subcapsular hematoma less than 10%', 'Parenchymal disruption 25-75%', 'Subcapsular hematoma greater than 75%', 'Minor laceration', 'Capsular tear only', 'B', 'Grade IV liver injury: parenchymal disruption involving 25-75% of hepatic lobe.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 14, 'Delayed splenic rupture can occur:', 'Within hours', 'Up to 2-3 weeks after injury', 'Never occurs', 'Only in children', 'Only with anticoagulation', 'B', 'Delayed splenic rupture can occur up to 2-3 weeks after injury, often from subcapsular hematoma rupture.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 15, 'Retroperitoneal hematoma in Zone 1 (central) should be:', 'Always explored', 'Never explored', 'Explored only if expanding', 'Observed only', 'Depends on mechanism', 'A', 'Zone 1 (central) retroperitoneal hematoma should always be explored due to major vascular structures.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 16, 'After splenectomy, vaccination is required against:', 'Influenza only', 'Pneumococcus, Meningococcus, H. influenzae', 'Hepatitis only', 'Tetanus only', 'No vaccines needed', 'B', 'Post-splenectomy patients need vaccination against encapsulated organisms: Pneumococcus, Meningococcus, H. influenzae.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 17, 'Duodenal injury is best diagnosed by:', 'Plain X-ray', 'CT with oral contrast', 'Ultrasound', 'Clinical examination only', 'MRI', 'B', 'CT with oral contrast best diagnoses duodenal injury, showing contrast leak or retroperitoneal air.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 18, 'Damage control laparotomy includes:', 'Complete definitive repair', 'Packing, temporizing measures, ICU resuscitation', 'Observation only', 'Delayed exploration', 'No surgery', 'B', 'Damage control involves abbreviated surgery (packing, control bleeding/contamination), ICU resuscitation, then definitive repair.'),
('d0000013-0000-0000-0000-000000000013'::uuid, 19, 'Bladder injury with gross hematuria and pelvic fracture is likely:', 'Extraperitoneal rupture', 'Intraperitoneal rupture', 'Urethral injury', 'Kidney injury', 'Ureteral injury', 'A', 'Pelvic fracture-associated bladder injury is usually extraperitoneal (bony fragment).'),
('d0000013-0000-0000-0000-000000000013'::uuid, 20, 'Angioembolization for solid organ injury is indicated when:', 'All injuries', 'Active bleeding on CT with hemodynamic stability', 'Always instead of surgery', 'Only for kidney', 'Never indicated', 'B', 'Angioembolization indicated for active bleeding (contrast blush) in hemodynamically stable patients.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000014-0000-0000-0000-000000000014'::uuid, 6, 'Immediately life-threatening chest injuries include all EXCEPT:', 'Tension pneumothorax', 'Massive hemothorax', 'Simple rib fracture', 'Cardiac tamponade', 'Open pneumothorax', 'C', 'Simple rib fracture is not immediately life-threatening. Tension pneumothorax, massive hemothorax, tamponade, open pneumothorax, flail chest are.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 7, 'Massive hemothorax is defined as:', 'Any blood in chest', 'Greater than 1500 mL initial or greater than 200 mL/hr', 'Greater than 500 mL', 'Greater than 100 mL', 'Less than 500 mL', 'B', 'Massive hemothorax: >1500 mL initial drainage or >200 mL/hr for 2-4 hours.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 8, 'Sucking chest wound should be covered with:', 'Completely occlusive dressing', 'Three-sided dressing', 'Nothing', 'Gauze only', 'Pressure bandage', 'B', 'Three-sided dressing allows air to escape during exhalation but prevents entry during inspiration.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 9, 'Chest tube for hemothorax should be placed:', 'Anteriorly at 2nd ICS', 'Posteriorly at 5th ICS midaxillary line', '7th ICS', 'Subxiphoid', 'Any location', 'B', 'Chest tube for hemothorax placed at 5th ICS midaxillary line, directed posteriorly and inferiorly.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 10, 'Pulmonary contusion is best diagnosed by:', 'Chest X-ray immediately', 'CT chest', 'Bronchoscopy', 'Thoracentesis', 'ECG', 'B', 'CT chest is most sensitive for pulmonary contusion. CXR findings may be delayed 4-6 hours.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 11, 'Widened mediastinum on chest X-ray suggests:', 'Pneumothorax', 'Aortic injury', 'Hemothorax', 'Rib fracture', 'Pulmonary contusion', 'B', 'Widened mediastinum (>8cm) is concerning for traumatic aortic injury. CT angiography required.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 12, 'Most common site of traumatic aortic injury is:', 'Ascending aorta', 'Aortic isthmus', 'Descending thoracic aorta', 'Abdominal aorta', 'Aortic root', 'B', 'Aortic isthmus (just distal to left subclavian) is most common site due to fixation point.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 13, 'Tracheobronchial injury should be suspected with:', 'Massive subcutaneous emphysema', 'Persistent air leak after chest tube', 'Hemoptysis', 'All of the above', 'None specifically', 'D', 'Suspect tracheobronchial injury with massive subcutaneous emphysema, persistent air leak, hemoptysis.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 14, 'Blunt cardiac injury (myocardial contusion) is best evaluated by:', 'Chest X-ray', 'ECG and echocardiography', 'CT scan', 'MRI', 'Angiography', 'B', 'ECG (arrhythmias) and echocardiography (wall motion abnormalities) evaluate blunt cardiac injury.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 15, 'Diaphragmatic injury is more common on:', 'Right side', 'Left side', 'Equal both sides', 'Centrally', 'Never diagnosed acutely', 'B', 'Left-sided diaphragmatic injury is more commonly diagnosed (liver protects right side).'),
('d0000014-0000-0000-0000-000000000014'::uuid, 16, 'Esophageal injury from trauma presents with:', 'Mediastinitis, pleural effusion', 'Pneumothorax only', 'Hemothorax only', 'No symptoms', 'Stridor', 'A', 'Esophageal injury leads to mediastinitis, left pleural effusion, and sepsis if not recognized.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 17, 'Pain relief for rib fractures is important to prevent:', 'Infection', 'Atelectasis and pneumonia', 'Bleeding', 'Scarring', 'Malunion', 'B', 'Adequate analgesia prevents splinting, atelectasis, and subsequent pneumonia.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 18, 'First and second rib fractures indicate:', 'Minor trauma', 'High-energy trauma with vascular injury risk', 'No significance', 'Lower rib injury', 'Liver injury', 'B', 'First and second rib fractures indicate significant force and risk of subclavian/brachiocephalic vascular injury.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 19, 'Scapular fracture indicates:', 'Minor trauma', 'Significant thoracic trauma', 'Shoulder dislocation', 'Clavicle fracture', 'Humerus fracture', 'B', 'Scapular fracture requires significant force and indicates severe thoracic trauma.'),
('d0000014-0000-0000-0000-000000000014'::uuid, 20, 'Emergency department thoracotomy is indicated for:', 'All chest trauma', 'Penetrating trauma with recent loss of signs of life', 'Stable hemothorax', 'Rib fractures', 'Pulmonary contusion', 'B', 'EDT indicated for penetrating thoracic trauma with loss of signs of life within past few minutes.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d0000015-0000-0000-0000-000000000015'::uuid, 6, 'Pericardial effusion causing tamponade may accumulate as little as:', '500 mL in acute setting', '100-200 mL in acute setting', '50 mL only', '1 liter minimum', '2 liters minimum', 'B', 'Acute pericardial effusion of 100-200 mL can cause tamponade if rapid. Chronic effusions tolerate more.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 7, 'Pulsus paradoxus is defined as:', 'Irregular pulse', 'SBP drop greater than 10 mmHg with inspiration', 'Weak pulse', 'Bounding pulse', 'Absent pulse', 'B', 'Pulsus paradoxus is SBP drop >10 mmHg during inspiration due to enhanced ventricular interdependence.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 8, 'ECG finding in cardiac tamponade:', 'ST elevation', 'Low voltage QRS and electrical alternans', 'Tall T waves', 'Wide QRS', 'Q waves', 'B', 'Low voltage QRS complexes and electrical alternans (beat-to-beat variation) are ECG signs.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 9, 'FAST examination for cardiac tamponade looks for:', 'Dilated ventricle', 'Pericardial effusion with right atrial collapse', 'Valvular abnormality', 'Wall motion abnormality', 'Aortic dissection', 'B', 'FAST shows pericardial effusion with right atrial or ventricular diastolic collapse.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 10, 'First-line treatment for traumatic cardiac tamponade is:', 'Pericardiocentesis', 'Immediate surgery (thoracotomy/sternotomy)', 'Observation', 'Diuretics', 'Fluid restriction', 'B', 'Traumatic tamponade requires surgery for definitive repair. Pericardiocentesis may temporize.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 11, 'Subxiphoid pericardial window allows:', 'Diagnosis only', 'Drainage and tissue for diagnosis', 'Complete repair', 'Bypass surgery', 'Valve replacement', 'B', 'Subxiphoid window provides drainage and pericardial tissue for diagnostic analysis.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 12, 'Most common cause of medical (non-traumatic) cardiac tamponade:', 'Malignancy', 'Uremia', 'Viral pericarditis', 'Tuberculosis', 'Myocardial infarction', 'A', 'Malignancy is the most common cause of large pericardial effusions causing tamponade.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 13, 'Kussmaul sign in cardiac tamponade refers to:', 'Hypotension', 'Paradoxical JVP rise with inspiration', 'Muffled heart sounds', 'Tachycardia', 'Pulsus paradoxus', 'B', 'Kussmaul sign is paradoxical rise in JVP during inspiration (seen in tamponade and constrictive pericarditis).'),
('d0000015-0000-0000-0000-000000000015'::uuid, 14, 'Fluid resuscitation in tamponade:', 'Is contraindicated', 'May temporarily improve filling', 'Worsens condition', 'Has no effect', 'Should be aggressive', 'B', 'Judicious fluid bolus may temporarily improve preload and cardiac output while preparing for drainage.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 15, 'Penetrating cardiac injury most commonly affects:', 'Right ventricle', 'Left ventricle', 'Right atrium', 'Left atrium', 'Aorta', 'A', 'Right ventricle is most commonly injured in penetrating cardiac trauma due to anterior position.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 16, 'Emergency pericardiocentesis needle is directed towards:', 'Right shoulder', 'Left shoulder', 'Sternum', 'Spine', 'Straight up', 'B', 'Needle directed towards left shoulder at 45-degree angle from subxiphoid position.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 17, 'Removal of small amount of pericardial fluid in tamponade:', 'Has no effect', 'Dramatically improves hemodynamics', 'Worsens condition', 'Requires large volume removal', 'Is dangerous', 'B', 'Removal of even 50 mL can dramatically improve hemodynamics in acute tamponade.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 18, 'Echocardiographic sign of hemodynamic compromise in effusion:', 'Right atrial collapse in diastole', 'Left ventricular hypertrophy', 'Mitral regurgitation', 'Aortic stenosis', 'VSD', 'A', 'Right atrial collapse during diastole indicates hemodynamic compromise from pericardial effusion.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 19, 'Constrictive pericarditis differs from tamponade in:', 'Chronic vs acute presentation', 'Calcification present', 'Surgical pericardiectomy treatment', 'All of the above', 'No difference', 'D', 'Constrictive pericarditis is chronic, may have calcification, and is treated with surgical pericardiectomy.'),
('d0000015-0000-0000-0000-000000000015'::uuid, 20, 'In cardiac tamponade, stroke volume is maintained by:', 'Increased heart rate', 'Decreased heart rate', 'Vasodilation', 'Diuresis', 'Reduced contractility', 'A', 'Tachycardia compensates for reduced stroke volume to maintain cardiac output initially.');
