INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d2000016-0000-0000-0000-000000000016'::uuid, 6, 'Most common level of cervical spine injury is:', 'C1-C2', 'C3-C4', 'C5-C6', 'C7-T1', 'C2-C3', 'C', 'C5-C6 and C6-C7 are the most common levels of cervical spine injury.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 7, 'Jefferson fracture involves:', 'Odontoid process', 'C1 ring burst fracture', 'Hangman fracture', 'C7 spinous process', 'Thoracic spine', 'B', 'Jefferson fracture is a burst fracture of C1 (atlas) ring from axial loading.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 8, 'Hangman fracture involves:', 'C1', 'C2 pars interarticularis', 'C3', 'Odontoid', 'C7', 'B', 'Hangman fracture is bilateral fracture of C2 pars interarticularis from hyperextension.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 9, 'Neurogenic shock differs from spinal shock in:', 'Neurogenic is cardiovascular, spinal is neurological', 'They are the same', 'Neurogenic is neurological', 'Spinal is cardiovascular', 'Neither causes hypotension', 'A', 'Neurogenic shock is cardiovascular (hypotension, bradycardia). Spinal shock is temporary loss of reflexes below injury.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 10, 'SCIWORA stands for:', 'Spinal Cord Injury With Obvious Radiographic Abnormality', 'Spinal Cord Injury Without Obvious Radiographic Abnormality', 'Surgical Care In Wound Repair', 'Spinal Column Injury With Osseous Repair', 'None of the above', 'B', 'SCIWORA: Spinal Cord Injury Without Obvious Radiographic Abnormality, common in children.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 11, 'Central cord syndrome affects:', 'Lower extremities more than upper', 'Upper extremities more than lower', 'One side only', 'Posterior columns', 'No motor deficit', 'B', 'Central cord syndrome affects upper extremities more than lower (arms weaker than legs).'),
('d2000016-0000-0000-0000-000000000016'::uuid, 12, 'Anterior cord syndrome results in:', 'Loss of motor, pain, temperature with preserved proprioception', 'Loss of proprioception only', 'Loss of posterior column function', 'No sensory loss', 'Complete paralysis only', 'A', 'Anterior cord syndrome: loss of motor function and pain/temperature sensation, preserved proprioception.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 13, 'Brown-Sequard syndrome results from:', 'Complete cord transection', 'Hemisection of spinal cord', 'Anterior cord injury', 'Central cord injury', 'Posterior cord injury', 'B', 'Brown-Sequard results from lateral hemisection causing ipsilateral motor/proprioception loss, contralateral pain/temperature loss.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 14, 'Priapism in spinal cord injury indicates:', 'Sacral sparing', 'Complete injury', 'Incomplete injury', 'Urinary retention', 'Autonomic dysfunction', 'B', 'Priapism in acute spinal cord injury usually indicates complete injury.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 15, 'Autonomic dysreflexia occurs in injuries above:', 'T6', 'T10', 'L1', 'S1', 'Any level', 'A', 'Autonomic dysreflexia occurs in injuries above T6 due to loss of sympathetic control.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 16, 'Log-roll technique is used to:', 'Assess spine while maintaining alignment', 'Treat spinal shock', 'Reduce fractures', 'Apply traction', 'Perform surgery', 'A', 'Log-roll maintains spinal alignment during examination and turning of trauma patients.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 17, 'Methylprednisolone in acute spinal cord injury is:', 'No longer routinely recommended', 'Mandatory treatment', 'Only for complete injuries', 'Only for incomplete injuries', 'Contraindicated', 'A', 'High-dose methylprednisolone is no longer routinely recommended due to lack of proven benefit.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 18, 'Unstable spine fracture requires:', 'Observation only', 'Surgical stabilization or external immobilization', 'Physical therapy', 'NSAIDs only', 'No treatment', 'B', 'Unstable fractures require surgical stabilization or rigid external immobilization.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 19, 'Chance fracture is associated with:', 'Cervical spine injury', 'Seat belt injury', 'Diving injury', 'Gunshot wound', 'Fall from height', 'B', 'Chance fracture (horizontal vertebral fracture) is associated with seat belt (lap belt) injury.'),
('d2000016-0000-0000-0000-000000000016'::uuid, 20, 'Sacral sparing indicates:', 'Complete spinal cord injury', 'Incomplete spinal cord injury', 'Cauda equina syndrome', 'Conus medullaris syndrome', 'Normal finding', 'B', 'Sacral sparing (perianal sensation, toe flexor function) indicates incomplete injury with better prognosis.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d2000017-0000-0000-0000-000000000017'::uuid, 6, 'Most common mechanism of pelvic fracture is:', 'Fall from height', 'Motor vehicle collision', 'Sports injury', 'Assault', 'Osteoporotic fracture', 'B', 'Motor vehicle collision is the most common cause of major pelvic fractures.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 7, 'Open book pelvic fracture results from:', 'Lateral compression', 'Anteroposterior compression', 'Vertical shear', 'Axial loading', 'Combined mechanism', 'B', 'Open book (external rotation) results from anteroposterior compression force.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 8, 'Bleeding in pelvic fractures is primarily from:', 'Arterial sources only', 'Venous plexus and cancellous bone', 'External sources', 'Muscle bleeding only', 'No significant bleeding', 'B', 'Most bleeding is from venous plexus and cancellous bone surfaces; arterial bleeding in 10-15%.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 9, 'Pelvic binder should be placed at level of:', 'Iliac crests', 'Greater trochanters', 'Umbilicus', 'Pubic symphysis', 'Any level', 'B', 'Pelvic binder is placed at level of greater trochanters to close pelvic ring.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 10, 'Urethral injury in pelvic fracture is indicated by:', 'Hematuria alone', 'Blood at urethral meatus, high-riding prostate', 'Normal voiding', 'Urinary frequency', 'Dysuria only', 'B', 'Blood at meatus, scrotal hematoma, high-riding prostate indicate urethral injury.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 11, 'Before catheterization in pelvic fracture:', 'No precautions needed', 'Retrograde urethrogram if urethral injury suspected', 'Always perform cystoscopy', 'Always insert suprapubic catheter', 'CT scan required', 'B', 'Retrograde urethrogram should be performed if urethral injury is suspected before catheterization.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 12, 'Young-Burgess classification is based on:', 'Fracture stability', 'Mechanism of injury', 'Associated injuries', 'Age of patient', 'Bone density', 'B', 'Young-Burgess classification categorizes pelvic fractures by mechanism: LC, APC, VS, Combined.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 13, 'Vertical shear pelvic fracture is characterized by:', 'Stable fracture', 'Hemipelvis displaced superiorly, highly unstable', 'Isolated pubic ramus fracture', 'Acetabular fracture only', 'Sacral fracture only', 'B', 'Vertical shear causes superior displacement of hemipelvis, highly unstable with high mortality.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 14, 'Angioembolization for pelvic hemorrhage is indicated when:', 'All pelvic fractures', 'Arterial bleeding identified on CT', 'Always before surgery', 'Only in children', 'Never indicated', 'B', 'Angioembolization indicated for arterial contrast extravasation on CT in hemodynamically responsive patients.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 15, 'Preperitoneal pelvic packing is used for:', 'Diagnostic purposes', 'Control of venous hemorrhage', 'Urological injury', 'Bowel injury', 'Nerve injury', 'B', 'Preperitoneal pelvic packing controls venous bleeding and creates tamponade effect.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 16, 'Morel-Lavallee lesion is:', 'Bone fragment', 'Degloving injury with fluid collection', 'Nerve injury', 'Vascular injury', 'Joint dislocation', 'B', 'Morel-Lavallee lesion is closed degloving injury with hematoma/seroma between fascia and skin.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 17, 'Tile classification of pelvic fractures is based on:', 'Mechanism', 'Stability: A stable, B rotationally unstable, C completely unstable', 'Age', 'Treatment required', 'Prognosis', 'B', 'Tile classification: A = stable, B = rotationally unstable, C = rotationally and vertically unstable.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 18, 'Sacral fractures are classified using:', 'Young-Burgess', 'Denis classification', 'Tile classification', 'AO classification', 'Gustilo classification', 'B', 'Denis classification divides sacrum into 3 zones based on location and neurological injury risk.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 19, 'Acetabular fracture affecting weight-bearing dome requires:', 'Conservative management', 'Surgical fixation for anatomical reduction', 'Casting only', 'Traction only', 'No treatment', 'B', 'Acetabular fractures involving weight-bearing dome require anatomical reduction and fixation.'),
('d2000017-0000-0000-0000-000000000017'::uuid, 20, 'Mortality rate in hemodynamically unstable pelvic fractures can be:', 'Less than 5%', '10-15%', '30-50%', '80-90%', 'Near 100%', 'C', 'Hemodynamically unstable pelvic fractures have high mortality rates of 30-50%.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d2000018-0000-0000-0000-000000000018'::uuid, 6, 'Most common location for compartment syndrome is:', 'Thigh', 'Forearm', 'Leg (anterior compartment)', 'Hand', 'Foot', 'C', 'Leg, particularly anterior compartment, is the most common location for compartment syndrome.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 7, 'Classic symptom of compartment syndrome is:', 'Numbness only', 'Pain out of proportion to injury, especially on passive stretch', 'Paralysis immediately', 'Pulselessness first', 'No pain', 'B', 'Pain out of proportion, especially with passive stretch of involved muscles, is classic early sign.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 8, 'The 6 Ps of compartment syndrome include all EXCEPT:', 'Pain', 'Pallor', 'Paresthesia', 'Pulselessness', 'Pruritus', 'E', 'The 6 Ps: Pain, Pallor, Paresthesia, Paralysis, Pulselessness, Pressure. Pruritus (itching) is not included.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 9, 'Absolute compartment pressure indicating fasciotomy is:', 'Greater than 10 mmHg', 'Greater than 20 mmHg', 'Greater than 30 mmHg', 'Greater than 50 mmHg', 'Greater than 100 mmHg', 'C', 'Absolute pressure >30 mmHg or delta pressure (diastolic minus compartment) <30 mmHg indicates fasciotomy.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 10, 'Delta pressure for fasciotomy is:', 'Systolic minus compartment pressure', 'Diastolic minus compartment pressure less than 30 mmHg', 'Mean arterial pressure', 'Compartment pressure alone', 'Pulse pressure', 'B', 'Delta pressure = Diastolic BP - Compartment pressure. <30 mmHg indicates fasciotomy needed.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 11, 'Fasciotomy should be performed within:', '2 hours', '6 hours', '12 hours', '24 hours', 'Any time', 'B', 'Fasciotomy within 6 hours prevents irreversible muscle necrosis. Delay worsens outcomes.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 12, 'Number of compartments in the leg is:', '2', '3', '4', '5', '6', 'C', 'Leg has 4 compartments: anterior, lateral, superficial posterior, deep posterior.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 13, 'Anterior compartment syndrome of leg affects which nerve?', 'Tibial', 'Deep peroneal (fibular)', 'Superficial peroneal', 'Saphenous', 'Sural', 'B', 'Deep peroneal nerve runs through anterior compartment; affected first (first web space numbness).'),
('d2000018-0000-0000-0000-000000000018'::uuid, 14, 'Two-incision fasciotomy of leg includes:', 'Medial and lateral incisions', 'Anterior and posterior incisions', 'Single incision only', 'Percutaneous release', 'Multiple small incisions', 'A', 'Two-incision technique: lateral (anterior and lateral compartments), medial (superficial and deep posterior).'),
('d2000018-0000-0000-0000-000000000018'::uuid, 15, 'Volkmann ischemic contracture is late complication of:', 'Thigh compartment syndrome', 'Forearm compartment syndrome', 'Leg compartment syndrome', 'Hand compartment syndrome', 'Foot compartment syndrome', 'B', 'Volkmann ischemic contracture is late sequela of forearm compartment syndrome with flexion deformity.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 16, 'Causes of compartment syndrome include all EXCEPT:', 'Fractures', 'Burns', 'Tight casts', 'Elevation of limb', 'Snake bite', 'D', 'Limb elevation is part of treatment, not a cause. Fractures, burns, casts, bites, reperfusion can cause CS.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 17, 'Crush syndrome is associated with:', 'Local compartment syndrome only', 'Systemic effects including hyperkalemia and myoglobinuria', 'No systemic effects', 'Hypocalcemia only', 'Anemia only', 'B', 'Crush syndrome causes hyperkalemia, myoglobinuria, acute kidney injury, metabolic acidosis.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 18, 'Treatment of crush syndrome includes:', 'No fluid resuscitation', 'Aggressive IV fluids, alkalinization, monitoring', 'Immediate amputation', 'Antibiotics only', 'Observation', 'B', 'Aggressive fluid resuscitation, urine alkalinization, and monitoring for hyperkalemia are essential.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 19, 'Pulselessness in compartment syndrome indicates:', 'Early stage', 'Very late stage with poor prognosis', 'Need for angiography', 'Arterial injury', 'Normal finding', 'B', 'Pulselessness is a late sign indicating severe compartment syndrome with poor prognosis for limb salvage.'),
('d2000018-0000-0000-0000-000000000018'::uuid, 20, 'After fasciotomy, wound management involves:', 'Immediate primary closure', 'Delayed primary closure or skin grafting', 'No wound care needed', 'Suturing immediately', 'Casting', 'B', 'Fasciotomy wounds are left open initially, then closed by delayed primary closure or skin grafting.');
INSERT INTO article_self_assessments (article_id, question_number, question_text, option_a, option_b, option_c, option_d, option_e, correct_option, explanation) VALUES
('d2000019-0000-0000-0000-000000000019'::uuid, 6, 'Rule of nines: head in adult represents:', '9%', '18%', '1%', '36%', '4.5%', 'A', 'In adults, head represents 9% TBSA. In children, head is larger percentage (up to 18%).'),
('d2000019-0000-0000-0000-000000000019'::uuid, 7, 'Each upper limb in rule of nines represents:', '9%', '18%', '1%', '4.5%', '36%', 'A', 'Each upper limb represents 9% TBSA (anterior arm 4.5%, posterior arm 4.5%).'),
('d2000019-0000-0000-0000-000000000019'::uuid, 8, 'Parkland formula for fluid resuscitation is:', '2 mL x kg x %TBSA', '4 mL x kg x %TBSA', '6 mL x kg x %TBSA', '1 mL x kg x %TBSA', '8 mL x kg x %TBSA', 'B', 'Parkland formula: 4 mL x body weight (kg) x %TBSA burn in first 24 hours.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 9, 'First half of calculated fluids should be given in:', 'First 2 hours', 'First 8 hours', 'First 24 hours', 'First 4 hours', 'Second 8 hours', 'B', 'Half of calculated fluids given in first 8 hours from time of burn (not from hospital arrival).'),
('d2000019-0000-0000-0000-000000000019'::uuid, 10, 'Preferred fluid for burn resuscitation is:', 'Normal saline', 'Lactated Ringers', 'Colloid', 'Dextrose', 'Albumin', 'B', 'Lactated Ringers is the preferred crystalloid for burn resuscitation.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 11, 'Escharotomy is indicated for:', 'All burns', 'Circumferential full-thickness burns', 'Superficial burns', 'Partial thickness burns', 'Sunburn', 'B', 'Escharotomy indicated for circumferential full-thickness burns causing vascular compromise or chest restriction.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 12, 'Inhalation injury should be suspected with:', 'Facial burns, singed nasal hairs', 'Carbonaceous sputum', 'Enclosed space fire', 'All of the above', 'None of the above', 'D', 'Suspect inhalation injury with facial burns, singed nasal hair, carbonaceous sputum, enclosed space.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 13, 'Carbon monoxide poisoning is treated with:', 'Hyperbaric oxygen', '100% oxygen', 'Room air', 'Low flow oxygen', 'Either A or B', 'E', 'High-flow 100% oxygen is initial treatment; hyperbaric oxygen for severe cases.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 14, 'Superficial partial thickness burn heals in:', '3 days', '10-14 days', '3-4 weeks', '6 weeks', 'Never heals spontaneously', 'B', 'Superficial partial thickness burns heal in 10-14 days without scarring.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 15, 'Full thickness burn is characterized by:', 'Blisters present', 'Pale, leathery, painless', 'Erythema only', 'Moist surface', 'Intact sensation', 'B', 'Full thickness burns are pale/white, leathery, insensate due to nerve destruction.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 16, 'Burn center referral criteria include:', 'Partial thickness greater than 10% TBSA', 'Burns to face, hands, feet, genitalia, joints', 'Inhalation injury', 'All of the above', 'Only full thickness burns', 'D', 'Referral for: >10% partial thickness, full thickness, special areas, inhalation, electrical, chemical burns.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 17, 'Curling ulcer is:', 'Skin ulcer from burn', 'Stress ulcer in burn patients', 'Decubitus ulcer', 'Arterial ulcer', 'Venous ulcer', 'B', 'Curling ulcer is stress-related gastroduodenal ulceration occurring in severe burns.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 18, 'Nutritional requirement in major burns is:', 'Normal caloric intake', 'Decreased caloric need', 'Increased protein and calories', 'NPO only', 'Low protein diet', 'C', 'Major burns cause hypermetabolic state requiring increased protein and caloric intake.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 19, 'Electrical burn injury differs from thermal in:', 'Only surface damage', 'Deep tissue damage greater than surface appearance', 'No cardiac effects', 'Less severe', 'Same treatment', 'B', 'Electrical burns cause deep tissue damage often underestimated by surface appearance.'),
('d2000019-0000-0000-0000-000000000019'::uuid, 20, 'Most common cause of death in burn patients is:', 'Fluid loss', 'Sepsis', 'Inhalation injury', 'Renal failure', 'Cardiac arrest', 'B', 'Sepsis is the most common cause of death in burn patients who survive initial resuscitation.');
